import React, { useState } from 'react';
import Video from '../../videos/keyboard.mp4'
import { Button } from '../ButtonElements';
import BackendURL from '../BackendURL'
import {
    HeroContainer,
    HeroBg,
    VideoBg,
    HeroContent,
    HeroH1,
    HeroP,
    HeroBtnWrapper,
    ArrowForward,
    ArrowRight
} from './HeroSectionElement';

const HeroSection = () => {
    const [hover, setHover] = useState(false);
    console.log(BackendURL.toString())
    const onHover = () => {
        setHover(!hover);
    }
    return (
        <HeroContainer>
            <HeroBg>
                <VideoBg autoPlay loop muted src={Video} type='video/mp4' />
            </HeroBg>
            <HeroContent>
                <HeroH1>We build a Time Machine</HeroH1>
                <HeroH1>for Personal Knowledge</HeroH1>
                <HeroP>Join our free Beta program now!</HeroP>
                <HeroBtnWrapper>
                    <Button
                        to='signup'
                        smooth={true}
                        duration={500}
                        spy={true}
                        exact='true'
                        offset={-80}
                        primary='true'
                        dark='true'
                        onMouseEnter={onHover}
                        onMouseLeave={onHover}
                    >
                        Get Started {hover ? <ArrowForward /> : <ArrowRight />}
                    </Button>
                </HeroBtnWrapper>
            </HeroContent>
        </HeroContainer>
    )
}

export default HeroSection
